def isqrt(x):
    """
    >>> isqrt(1)
    1
    >>> isqrt(16)
    4
    >>> isqrt(100000000)
    10000
    """
    x = int(x)
    if x <= 0:
        raise ValueError("isqrt is only defined for positive integers")
    if x == 1:
        return 1
    lo = 1
    hi = 2 ** (x.bit_length() // 2)
    while lo < hi:
        mid = (lo + hi + 1) // 2
        if mid * mid > x:
            hi = mid - 1
        else:
            lo = mid
    return lo
