func countOperationsToEmptyArray(nums []int) int {
    n := len(nums)
    id := make([]int, n)
    for i := range id {
        id[i] = i
    }
    sort.Slice(id, func(i, j int) bool {
        return nums[id[i]] < nums[id[j]]
    })
    ans := n
    // 先把 n 计入答案
    t := BIT{tree: make([]int, n+1)}
    // 下标从 1 开始
    pre := 1
    // 上一个最小值的位置，初始为 1
    for k := 0; k < n; k++ {
        i := id[k] + 1
        // 下标从 1 开始
        if i >= pre { // 从 pre 移动到 i，跳过已经删除的数
            ans += i - pre
        } else { // 从 pre 移动到 n，再从 1 移动到 i，跳过已经删除的数
            ans += n - pre + i
        }
        t.inc(i)
        // 删除 i
        pre = i
    }
    return ans
}

type BIT struct {
    tree []int
}

func (t *BIT) inc(i int) {
    for i < len(t.tree) {
        t.tree[i]++
        i += i & -i
    }
}

func (t *BIT) sum(x int) int {
    var res int
    for x > 0 {
        res += t.tree[x]
        x &= x - 1
    }
    return res
}

func (t *BIT) query(left, right int) int {
    return t.sum(right) - t.sum(left - 1)
}
