                if (row.size() == ans.size() + 1 || it->second == 1)
                    ans.push_back(row), row.clear();
                if (it->second == 1)
                    cnt.erase(it++);
                else
                    ++it;
            }
        }
        return ans;
    }
};

#include<bits/stdc++.h>
using namespace std;

class Solution {
public:
  vector<int> minReverseOperations(int n, int p, vector<int>& banned, int k) {
    unordered_set<int> ban{banned.begin(), banned.end()};
    set<int> sets[2];
    for (int i = 0; i < n; ++i)
      if (!ban.count(i))
        sets[i % 2].insert(i);
    sets[0].insert(n);
    sets[1].insert(n);
    // 哨兵
    vector<int> ans(n, -1);
    vector<int> q = {p};
    for (int step = 0; !q.empty(); ++step) {
      vector<int> nq;
      for (int i : q) {
        ans[i] = step;
        // 从 mn 到 mx 的所有位置都可以翻转到
        int mn = max(i - k + 1, k - i - 1);
        auto &s = sets[mn % 2];
        for (auto it = s.lower_bound(mn); *it <= mx; it = s.erase(it))
          nq.push_back(*it);
      }
      q = move(nq);
    }
    return ans;
  }
};

#include <bits/stdc++.h>
using namespace std;
int main()
{
  vector<int> ans(5, -1);
  for(int i = 0; i < 5; i++)
    cout << ans[i] << endl;
}
