class Solution {
    public int findChampion(int n, int[][] edges) {
        int[] degree = new int[n];
        for (int[] e : edges) {
            // add edge to degree of each node
            degree[e[1]]++;
        }
        int champion = -1;
        for (int i = 0; i < n; i++) {
            if (degree[i] == 0) {
                if (champion == -1) {
                    champion = i;
                } else {
                    return -1;
                }
            }
        }
        return champion;
    }
}

def maximumScoreAfterOperations(edges: List[List[int]], values: List[int]) -> int:
    # Initialize graph
    size = len(values)
    graph = [[] for _ in range(size)]
    for edge in edges:
        graph[edge[0]].append(edge[1])
        graph[edge[1]].append(edge[0])

    # Initialize scores
    score = 0
    for i in range(size):
        score += values[i]

    # DFS
    memo = [None for _ in range(size)]
    ans = 0
    def dfs(x: int, fa: int) -> int:
        if memo[x] != None:
            return memo[x]

        loss = 0
        for y in graph[x]:
            if y != fa:
                loss += dfs(y, x)

        if len(graph[x]) == 1:
            memo[x] = values[x]
            return values[x]
        else:
            memo[x] = min(values[x], loss)
            return min(values[x], loss)

    for i in range(size):
        ans += dfs(i, -1)

    return ans

