func countOfPairs(n, x, y int) []int {
    w := make([][]int, n + 1)
    INF := int(^uint(0) >> 1)
    for i := 0; i <= n; i++ {
        w[i] = make([]int, n + 1)
        for j := 0; j <= n; j++ {
            w[i][j] = INF
        }
        w[i][i] = 0
    }
    if x != y {
        w[x][y] = 1
        w[y][x] = 1
    }
    for i := 0; i < n; i++ {
        w[i][i + 1] = 1
        w[i + 1][i] = 1
    }
    for p := 0; p <= n; p++ {
        for st := 0; st <= n; st++ {
            for end := 0; end <= n; end++ {
                w[st][end] = min(w[st][end], w[st][p] + w[p][end])
            }
        }
    }
    res := make([]int, n + 1)
    for i := 0; i <= n; i++ {
        for j := 0; j <= n; j++ {
            if w[i][j] != INF {
                res[w[i][j]]++
            }
        }
    }
    result := make([]int, n)
    for i := 0; i < n; i++ {
        result[i] = res[i + 1]
    }
    return result
}

