class Solution:

    def maxSum(self, root, parent=None):
        if root is None:
            return 0, 0

        left_sum, left_max = self.maxSum(root.left, root)
        right_sum, right_max = self.maxSum(root.right, root)

        root.sum = root.val + left_sum + right_sum

        max_child = max(left_max, right_max)
        root.max = max(root.val, left_sum + right_sum + root.val,
                       max_child, root.sum - root.val + max_child)

        max_height = 1 + max(left_max, right_max)

        if parent != None:
            if parent.left == root:
                parent.max = max(parent.max, root.max)
            if parent.right == root:
                parent.sum = max(parent.sum, root.sum)

        return root.sum, max_height

    def deleteTreeNodes(self, nodes: int, parent: List[int], value: List[int]) -> int:
        if nodes == 1:
            return 1

        root = None
        tree = {}

        for i in range(nodes):
            if parent[i] == -1:
                root = i
                tree[i] = TreeNode(value[i], None, None)
            else:
                if parent[i] not in tree:
                    tree[parent[i]] = TreeNode(value[parent[i]], None, None)
                tree[parent[i]].left = TreeNode(value[i], None, None)

        self.maxSum(tree[root])

        return tree[root].max

