class Solution {
public:
    string smallestBeautifulString(string s, int k) {
        string t(s);
        sort(t.begin(), t.end());
        while(t<s) {
            int n=s.size();
            int cnt=1;
            int idx=0;
            while(idx<n) {
                while(idx<n-1 && s[idx]==s[idx+1]) idx++;
                if(idx!=n-1) cnt++;
                idx++;
            }
            if(cnt==k) {
                return t;
            }
            int i=t.size()-2;
            while(i>=0 && t[i]==t[i+1]) i--;
            t[i]=t[i]+1;
            t[i+1]='a';
        }
        return t;
    }
};

