class Solution {
public:
    int findMaxFish(vector<vector<int>> &grid) {
        int m = grid.size(), n = grid[0].size(), ans = 0;
        function<int(int, int)> dfs = [&](int x, int y) -> int {
            if (grid[x][y] == 0)
                return 0;
            int sum = grid[x][y];
            grid[x][y] = 0; //标记成访问过
            for (auto &d : dirs) {
                if (x + d[0] >= 0 && x + d[0] < m && y + d[1] >= 0 && y + d[1] < n) {
                    sum += dfs(x + d[0], y + d[1]);
                }
            }
            return sum;
        };
        for (int i = 0; i < m; ++i)
            for (int j = 0; j < n; ++j)
                ans = max(ans, dfs(i, j));
        return ans;
    }
private:
    static constexpr int dirs[4][2] = {{-1, 0}, {1, 0}, {0, -1}, {0, 1}};
};
