class Solution {
public:
  long long countOperationsToEmptyArray(vector<int> &nums) {
    int n = nums.size(), id[n];
    iota(id, id + n, 0);
    sort(id, id + n, [&](int i, int j) { return nums[i] < nums[j]; });
    long long ans = n;
    // 先把 n 计入答案
    BIT t(n + 1);
    // 下标从 1 开始
    int pre = 1;
    // 上一个最小值的位置，初始为 1
    for (int k = 0; k < n; ++k) {
      int i = id[k] + 1;
      // 下标从 1 开始
      if (i >= pre) // 从 pre 移动到 i，跳过已经删除的数
        ans += t.query(i + 1, n) + t.sum(i);
      else // 从 pre 移动到 n，再从 1 移动到 i，跳过已经删除的数
        ans += t.query(pre + 1, n) + t.query(1, i) + t.sum(i);
      t.inc(i); // 删除 i
      pre = i;
    }
    return ans;
  }
};

