package leetcode407;

public class No_of_Submatrices_with_Only_X {
    public int numSubmat(char[][] mat) {
        int row = mat.length;
        int col = mat[0].length;
        int [][] freq = new int[row][col];

        for(int i = 0; i < row; i++){
            for(int j = 0; j < col; j++){
                if(mat[i][j] == 'X'){
                    freq[i][j] = (j > 0? freq[i][j-1] : 0) + 1;
                }
            }
        }

        int ans = 0;
        for(int i = 0; i < row; i++){
            for(int j = 0; j < col; j++){
                for(int k = j; k < col; k++){
                    if(mat[i][k] == '.') continue;

                    int total = freq[i][k];
                    if(j > 0) total -= freq[i][j - 1];

                    if(total > 0) ans++;

                }
            }
        }
        return ans;
    }
}

