def check(data):
    try:
        node = ast.parse(data)
        if not isinstance(node, ast.Module):
            return {"syntax_errors": "Not a module"}
        visitor = Visitor()
        visitor.visit(node)
        return {
            "performance_issues": visitor.performance_issues,
            "security_issues": visitor.security_issues,
            "syntax_errors": visitor.syntax_errors,
            "logical_errors": visitor.logical_errors
        }
    except SyntaxError:
        return {"syntax_errors": "Syntax Error"}
    except Exception as e:
        return {"syntax_errors": str(e)}


class Visitor(ast.NodeVisitor):
    def __init__(self):
        self.performance_issues = []
        self.security_issues = []
        self.syntax_errors = []
        self.logical_errors = []

    def visit_Call(self, node):
        if isinstance(node.func, ast.Name):
            name = node.func.id
            if name in ['eval', 'exec', 'input', 'raw_input']:
                self.security_issues.append(name)
            if name in ['sleep', 'wait']:
                self.performance_issues.append(name)
        self.generic_visit(node)

    def visit_Name(self, node):
        if node.id == 'assert':
            self.logical_errors.append('assert')
        self.generic_visit(node)

