class Solution {
    public boolean doesAliceWin(String s) {
        int[] count = new int[2];
        for (char c : s.toCharArray()) {
            if (c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u') {
                count[0]++;
            } else {
                count[1]++;
            }
        }
        return count[0] > count[1];
