class Solution {
public:
    long long countOperationsToEmptyArray(vector<int> &nums) {
        int n = nums.size(), id[n];
        iota(id, id + n, 0);
        sort(id, id + n, [&](int i, int j) {return nums[i] < nums[j];});
        long long ans = n;
        BIT t(n + 1);
        int pre = 1;
        for (int k = 0; k < n; ++k) {
            int i = id[k] + 1;
            if (i >= pre) {
                t.inc(i);
            } else {
                t.inc(i);
                t.inc(pre);
            }
            pre = i;
        }
        return ans;
    }
};

