class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        ans = [0] * len(queries)
        father = list(range(n))

        def find(x: int) -> int:
            if father[x] != x:
                father[x] = find(father[x])
            return father[x]

        for i in range(len(queries)):
            u, v = queries[i]
            father[find(v)] = find(u)
            ans[i] = min(ans[i], abs(v - u) + 1)
        for i in range(n - 1, -1, -1):
            if father[i] != i:
                ans[find(i)] += ans[i]
        return ans
