class Solution {
public:
    int addMinimum(string word) {
        int n = word.size();
        vector<int> d(n + 1);
        for (int i = 1; i <= n; i++) {
            d[i] = d[i - 1] + 1;
            if (word[i - 1] == 'a') {
                d[i] = min(d[i], d[i - 2] + 1);
            }
            if (word[i - 1] == 'b') {
                d[i] = min(d[i], d[i - 1] + 1);
                d[i] = min(d[i], d[i - 2] + 1);
