            if (word[i - 1] == 'c') {
                d[i] = min(d[i], d[i - 1] + 1);
                d[i] = min(d[i], d[i - 2] + 1);
                d[i] = min(d[i], d[i - 3] + 1);
            }
        }
        return d[n];
    }
};

