class Solution {
public:
    TreeNode* replaceValueInTree(TreeNode* root) {
        vector<TreeNode*> q = {root};
        root->val = 0;
        while (!q.empty()) {
            vector<TreeNode*> q2;
            int sum = 0;
            for (auto fa : q) {
                if (fa->left) {
                    q2.push_back(fa->left);
                    fa->left->val = sum;
                }
                if (fa->right) {
                    q2.push_back(fa->right);
                    fa->right->val = sum;
                }
            }
            for (auto fa : q) {
                int child_sum = (fa->left ? fa->left->val : 0) +(fa->right ? fa->right->val : 0);
                if (fa->left) {
                    fa->left->val = child_sum;
                }
                if (fa->right) {
                    fa->right->val = child_sum;
                }
            }
            q = move(q2);
        }
        return root;
    }
};
