class Solution {
public:
    vector<long long> distance(vector<int> &nums) {
        int n = nums.size();
        unordered_map<int, vector<int>> groups;
        for (int i = 0; i < n; ++i)groups[nums[i]].push_back(i); // 相同元素分到同一组，记录下标
        vector<long long> ans(n);
        long long s[n + 1];
        s[0] = 0;
        for (auto &[_, a]: groups) {
            int m = a.size();
            for (int i = 0; i < m; ++i)s[i + 1] = s[i] + a[i]; // 前缀和
            for (int i = 0; i < m; ++i) {
                long long target = a[i];
                ans[target] = s[m] - s[i] - target * (m - i); // 蓝色面积 + 绿色面积
            }
        }
        return ans;
    }
};

Input: nums = [5,2,1,4,3], p = 3
Output: 1
Explanation:
We can choose the pairs (2, 4), (3, 4) and (3, 5) to minimize the maximum difference.
Note that we cannot choose (1, 5) since the index 1 appears more than once in our p pairs.


class Solution {
public:
    int minimizeMax(vector<int> &nums, int p) {
        sort(nums.begin(), nums.end());
        int left = -1, right = nums.back() - nums[0];
        // 开区间
        while (left + 1 < right) {
            // 开区间
            int mid = left + (right - left) / 2;
            int cnt = 0;
            // 都选
            for (int i = 0; i < nums.size() - 1; ++i) {
                if (nums[i + 1] - nums[i] > mid) {
                    cnt++;
                    ++i;
                }
            }
            // (cnt >= p ? right : left) = mid;
            (cnt >= p ? right : left) = mid;
        }
        return right;
    }
};
