class Solution:
    def nonSpecialCount(self, l: int, r: int) -> int:
        return len([x for x in range(l, r + 1) if self.isSpecial(x) == False])

    def isSpecial(self, n):
        if n == 1:
            return False
        if n == 2:
            return True
        if n == 4:
            return True
        if n % 2 == 0:
            return False
        for i in range(3, int(n ** 0.5) + 1, 2):
            if n % i == 0:
                return False
        return True
