class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        fa = list(range(n - 1))

        # 非递归并查集
        def find(x: int) -> int:
            if x != fa[x]:
                fa[x] = find(fa[x])
            return fa[x]

        ans = [0] * len(queries)
        for i, q in enumerate(queries):
            u, v = q
            u0 = find(u)
            v0 = find(v)
            if u0 != v0:
                fa[u0] = v0
                ans[i] = n - 1
            else:
                ans[i] = n - 1 - (len(set(fa)) - 1)
        return ans
