class Solution:
    def sumCounts(self, nums: List[int]) -> int:
        n = len(nums)
        sum = [0] * (n * 4)
        todo = [0] * (n * 4)

        def do(o: int, l: int, r: int, add: int) -> None:
            sum[o] += add * (r - l + 1)
            todo[o] += add

        def query_and_add1(o: int, l: int, r: int, L: int, R: int) -> int:
            if L <= l and r <= R:
                return sum[o] + todo[o] * (r - l + 1)
            m = (l + r) >> 1
            if R <= m:
                return query_and_add1(o << 1, l, m, L, R)
            elif m < L:
                return query_and_add1(o << 1 | 1, m + 1, r, L, R)
            else:
                return (
                    query_and_add1(o << 1, l, m, L, R)
                    + query_and_add1(o << 1 | 1, m + 1, r, L, R)
                )

        for i, x in enumerate(nums):
            do(1, 0, n - 1, x)
            nums[i] = query_and_add1(1, 0, n - 1, 0, x - 1)
        for i in range(n - 1, -1, -1):
            nums[i] = query_and_add1(1, 0, n - 1, nums[i], n - 1)
        return sum[1]

