class FenwickTree:
    def __init__(self, n):
        self.size = n
        self.data = [0] * (n + 1)

    def lowbit(self, x):
        return x & (-x)

    def update(self, pos, val):
        while pos <= self.size:
            self.data[pos] += val
            pos += self.lowbit(pos)

    def query(self, k):
        ans = 0
        while k:
            ans += self.data[k]
            k -= self.lowbit(k)
        return ans, self.size - k

