class Solution:
    def sumCounts(self, nums: List[int]) -> int:
        n = len(nums)
        sum = [0] * (n * 4)
        todo = [0] * (n * 4)

        def do(o: int, l: int, r: int, add: int) -> None:
            sum[o] += add * (r - l + 1)
            todo[o] += add

        # o=1  [l,r] 1<=l<=r<=n
        # 把 [L,R] 加一，同时返回加一之前的区间和
        def query_and_add1(o: int, l: int, r: int, L: int, R: int) -> int:
            if L > R: return 0
            if l == L and r == R:
                do(o, l, r, 1)
                return sum[o]
            m = (l + r) // 2
            o *= 2
            if R <= m: return query_and_add1(o, l, m, L, R)
            if L > m: return query_and_add1(o + 1, m + 1, r, L, R)
            return query_and_add1(o, l, m, L, m) + query_and_add1(o + 1, m + 1, r, m + 1, R)

        ans = s = 0
        last = {}
        for i, x in enumerate(nums, 1):
            j = last.get(x, 0)
            s += query_and_add1(1, 1, n, j + 1, i) * 2 + i - j
            ans += s
            last[x] = i
        return ans % 1_000_000_007

