class FenwickTree:
    def __init__(self, n: int) -> None:
        self.n = n
        self.t = [0] * (n + 1)

    def update(self, pos: int, val: int) -> None:
        pos += 1
        while pos <= self.n:
            self.t[pos] += val
            pos += pos & (-pos)

    def query(self, pos: int) -> tuple[int, int]:
        pos += 1
        ans = 0
        while pos > 0:
            ans += self.t[pos]
            pos -= pos & (-pos)
        return ans, self.n - ans


