        int cnt = -1, ans = 0;
        for (int i = 0; i < divisors.size(); i++) {
            int tmp = 0;
            for (int j = 0; j < nums.size(); j++) {
                if (nums[j] % divisors[i] == 0) {
                    tmp++;
                }
            }
            if (tmp > cnt || (tmp == cnt && divisors[i] < ans)) {
                cnt = tmp;
                ans = divisors[i];
            }
        }
        return ans;
    }
};

Example 1:
Input: nums = [10,2,8,9]
Output: [10,12,20,30]
Explanation: The array arr is [10, 10 + 2, 10 + 2 + 8, 10 + 2 + 8 + 9].
The conversion array is [10, 12, 20, 30].
The score is 10 + 12 + 20 + 30 = 72.
The prefix scores are [10, 22, 42, 72].

Example 2:
Input: nums = [4,3,2,6]
Output: [4,7,9,15]
Explanation: The array arr is [4, 4 + 3, 4 + 3 + 2, 4 + 3 + 2 + 6].
The conversion array is [4, 7, 9, 15].
The score is 4 + 7 + 9 + 15 = 35.
The prefix scores are [4, 11, 20, 35].







class Solution {
public:
    vector<long long> findPrefixScore(vector<int>& nums) {
            vector<long long> ans;
            long long tmp = nums[0];
            ans.push_back(nums[0] * 2);
            for (int i = 1; i < nums.size(); ++i) {
                    if (nums[i] > tmp) tmp = nums[i];
                    ans.push_back(ans[i-1]+nums[i]);
            }
            return ans;
    }
};

Input: nums = [2,1,1,2,3,1,2,1,1,3,2,1,3,2]
Output: [4,2,1,5,7,2,1,4,2,5,3,2,1,3,2]







class Solution {
public:
    vector<long long> distance(vector<int>& nums) {
        int n = nums.size();
        unordered_map<int, vector<int>> groups;
        for (int i = 0; i < n; ++i)groups[nums[i]].push_back(i);
        // 相同元素分到同一组，记录下标
        vector<long long> ans(n);
        long long s[n + 1];
        s[0] = 0;
        for (auto& [_, a] : groups) {
            int m = a.size();
            for (int i = 0; i < m; ++i)s[i + 1] = s[i] + a[i];
            // 前缀和
            for (int i = 0; i < m; ++i) {
                long long target = a[i];
                long long left = s[i];
                long long right = s[m] - s[i] - target * (m - i);
                ans[a[i]] = left + right;
                // 蓝色面积
            }
        }
        return ans;
    }
};
