package com.javaaid.hackerrank.solutions.datastructures.arrays;

public class TriangleType {
	public static String triangleType(int[] nums) {
		Arrays.sort(nums);
		int x = nums[0];
		int y = nums[1];
		int z = nums[2];
		if (x + y <= z) {
			// 排序后，只需比较 x+y 和 z
			return "none";
		}
		if (x == z) {
			// 排序了，说明 y 也和 x z 相等
			return "equilateral";
		}
		if (x == y) {
			return "isosceles";
		}
		return "scalene";
	}
}

