import java.util.Arrays;

public class Point {
    int x;
    int y;

    public Point(int x, int y) {
        this.x = x;
        this.y = y;
    }
}

class Solution {
    public int numberOfPairs(int[][] points) {
        int pairs = 0;
        Arrays.sort(points, (a, b) -> {
            if (a[0] != b[0]) {
                return a[0] - b[0];
            } else {
                return b[1] - a[1];
            }
        });
        int n = points.length;
        Point[] ps = new Point[n];
        for (int i = 0; i < n; i++) {
            ps[i] = new Point(points[i][0], points[i][1]);
        }
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                if (isGood(ps, i, j)) {
                    pairs++;
                }
            }
        }
        return pairs;
    }

    public boolean isGood(Point[] ps, int index1, int index2) {
        int n = ps.length;
        for (int i = 0; i < n; i++) {
            if (i == index1 || i == index2) {
                continue;
            }
            if (ps[i].x >= ps[index1].x && ps[i].x <= ps[index2].x && ps[i].y >= ps[index2].y && ps[i].y <= ps[index1].y) {
                return false;
            }
        }
        return true;
    }
}

