class Solution:
    def nonSpecialCount(self, l: int, r: int) -> int:
        return len([i for i in range(l, r + 1) if self.getDivisorCount(i) != 2])

    def getDivisorCount(self, num):
        return len(set(self.getDivisor(num)))

    def getDivisor(self, num):
        divisors = [1]
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                divisors.append(i)
                divisors.append(num // i)
        return divisors

