class FenwickTree:
    def __init__(self, n):
        self.sums = [0] * (n + 1)

    def add(self, index, delta):
        while index < len(self.sums):
            self.sums[index] += delta
            index += index & (-index)

    def query(self, index):
        ans = 0
        while index > 0:
            ans += self.sums[index]
            index -= index & (-index)
        return ans

