class Solution:
    def minChanges(self, s: str) -> int:
        n = len(s)
        # dp[i][j] represents the minimum number of changes to get s[:i] and t[:j]
        dp = [[float("inf")] * (n + 1) for _ in range(n + 1)]
        for i in range(n + 1):
            dp[i][i] = 0

        for i in range(n):
            for j in range(i + 1, n):
                # if the i-th and j-th characters of s and t are equal, we can add 0 to the dp[i][j]
                if s[i] == s[j]:
                    dp[i + 1][j + 1] = dp[i][j]
                # if the i-th and j-th characters of s and t are not equal, we can add 1 to the dp[i][j]
                else:
                    dp[i + 1][j + 1] = dp[i][j] + 1

        return dp[n][n]
