import java.util.Arrays;

public class Solution {
    public String triangleType(int[] nums) {
        Arrays.sort(nums);
        int x = nums[0];
        int y = nums[1];
        int z = nums[2];
        if (x + y <= z) { // 排序后，只需比较 x+y 和 z
            return "none";
        }
        if (x == z) { // 排序了，说明 y 也和 x z 相等
            return "equilateral";
        }
        if (x == y || y == z) { // 如果 x 等于 y 或者 y 等于 z
            return "isosceles";
        }
        return "scalene";
    }
}

