public class Solution {
    public int numberOfSubstrings(String s) {
        char[] s1 = s.toCharArray();
        int n = s1.length;
        int m = 0;
        int[] a = new int[n + 1];
        for (int i = 0; i < n; i++) {
            if (s1[i] == '0') {
                a[m++] = i;
            }
        }
        int tot1 = n - m;
        a[m] = n; // 哨兵
        int tot2 = 0;
        int i = 0; // >= left 的第一个 0 的下标是 a[i]
        for (int left = 0; left < n; left++) {
            if (s1[left] == '1') {
                tot2 += a[i] - left; // 不含 0 的子串个数
            }
            for (int k = i; k < m; k++) {
                int cnt0 = k - i + 1;
                if (a[k] - left > cnt0 * cnt0) {
                    break;
                }
                int cnt1 = a[k] - left - (k - i);
                tot2 += cnt0 * cnt1;
            }
            if (s1[left] == '0') {
                i++; // 这个 0 后面不会再枚举到了
            }
        }
        return tot2;
    }
}

