                # 修改前，先去掉结束位置
                if a[pre] == a[i]:
                    remove(pre)
                if a[i] == a[nxt]:
                    remove(i)
                a[i] = c
                # 修改后，添加新的结束位置
                if a[pre] == a[i]:
                    add(pre)
                if a[i] == a[nxt]:
                    add(i)
