int query_and_add1(int o, int l, int r, int L, int R){
    if(l == L && r == R) return sum[o] + (L-R) * todo[o];
    int mid = (l+r)/2;
    int ret = 0;
    if(mid >= L) ret += query_and_add1(o*2, l, mid, L, R);
    if(mid < R) ret += query_and_add1(o*2+1, mid+1, r, L, R);
    return ret;
}

