class Solution {
public:
    int addMinimum(string word) {
        int n = word.size();
        vector<int> d(n + 1);
        for (int i = 1; i <= n; i++) {
            if (word[i - 1] == 'a' || word[i - 1] == 'b')
                d[i] = d[i - 1] - 1;
            else if (word[i - 1] == 'c' && d[i - 1] == 0)
                d[i] = 2;
            else
                d[i] = d[i - 1];
        }
        return d[n];
    }
};
