class Solution {
public:
    int longestSemiRepetitiveSubstring(string s) {
        int ans = 1, left = 0, same = 0, n = s.length();
        for (int right = 1; right < n; right++) {
            if (s[right] == s[right - 1] && ++same > 1) {
                same = 1;
                ans = max(ans, right - left + 1);
            }
            if (s[right] != s[right - 1]) {
                ans = max(ans, right - left + 1);
                left = right;
                same = 0;
            }
        }
        return ans;
    }
};

Input: n = 2, queries = [[0,1,2],[1,0,1]]
Output: 3
Explanation: At the beginning, the 2 x 2 matrix is [[0, 0],[0, 0]].
Following the first query, the matrix becomes [[0, 2],[0, 0]].
Following the second query, the matrix becomes [[0, 2],[1, 1]].
Thus, the sum of integers in the matrix is 0 + 2 + 1 + 1 = 3, and we return 3.
Constraints:
1 <= n <= 100
1 <= queries.length <= 105
queries[i].length == 3
0 <= typei <= 1
0 <= indexi, vali <= n - 1



class Solution {
public:
    long long matrixSumQueries(int n, vector<vector<int>>& queries) {
        long long ans = 0;
        unordered_set<int> vis[2];
        for (int i = queries.size() - 1; i >= 0; i--) {
            auto &q = queries[i];
            int type = q[0], index = q[1], val = q[2];
            if (!vis[type].count(index)) { // 后面（>i）没有对这一行/列的操作
                // 这一行/列还剩下 n-vis[type^1].size() 个可以填入的格子
                vis[type].insert(index);
            }
        }
        return ans;
    }
};

