class Solution {
public:
    string smallestBeautifulString(string s, int k) {
        unordered_set<string> set;
        string cur = s;
        while (set.count(cur) == 0) {
            set.insert(cur);
            if (cur.size() >= k) break;
            for (int i = 1; i <= 3; ++i) {
                string next = cur;
                next.push_back('a' + i - 1);
                set.insert(next);
                if (next.size() >= k) break;
            }
            string temp;
            for (int i = 1; i < cur.size(); ++i) {
                temp.push_back(cur[i]);
            }
            cur = temp;
        }
        return cur;
    }
};

