int query(int o, int L, int R, int l, int r) {
    if (L <= l && r <= R) return sum[o];
    int m = (L + R) >> 1, add = todo[o];
    if (add) {
        todo[o * 2] += add;
        todo[o * 2 + 1] += add;
        todo[o] = 0;
    }
    int res = 0;
    if (l <= m) res += query(o * 2, L, m, l, r);
    if (r > m) res += query(o * 2 + 1, m + 1, R, l, r);
    sum[o] = sum[o * 2] + sum[o * 2 + 1];
    return res;
}

void add(int o, int L, int R, int l, int r, int add) {
    if (L <= l && r <= R) {
        sum[o] += add * (r - l + 1);
        todo[o] += add;
        return;
    }
    int m = (L + R) >> 1;
    if (todo[o]) {
        add(o * 2, L, m, L, R, todo[o]);
        add(o * 2 + 1, m + 1, R, L, R, todo[o]);
        todo[o] = 0;
