        function<int(int, int)> dfs = [&](int x, int y) -> int {
            if (grid[x][y] <= 0)return 0;
            int sum = grid[x][y];
            grid[x][y] = 0; // 标记成访问过
            for (auto &d: dirs) {
                int r = x + d[0], c = y + d[1];
                if (r >= 0 && r < m && c >= 0 && c < n) {
                    sum += dfs(r, c);
                }
            }
            return sum;
        };
        for (int i = 0; i < m; ++i)for (int j = 0; j < n; ++j)ans = max(ans, dfs(i, j));
        return ans;
    }
};
