class Solution:
    def maximizeSquareArea(self, m: int, n: int, hFences: List[int], vFences: List[int]) -> int:
        self.ans = -1
        hf, vf = sorted(set(hFences)), sorted(set(vFences))
        for i in range(len(hf)):
            if i != 0 and hf[i] - hf[i - 1] < 2: continue
            for j in range(len(vf)):
                if j != 0 and vf[j] - vf[j - 1] < 2: continue
                self.dfs(i, j, hf, vf, m, n)
        return self.ans % (10 ** 9 + 7)

    def dfs(self, h, v, hf, vf, m, n):
        if h == len(hf) and v == len(vf):
            self.ans = max(self.ans, (m - 1 - hf[h - 1]) * (n - 1 - vf[v - 1]))
            return
        if h == len(hf):
            self.dfs(h, v + 1, hf, vf, m, n)
