```cpp
#include <vector>
#include <iostream>
#include <algorithm>

using namespace std;

class Solution {
public:
    int minimizeMax(vector<int> &nums, int p) {
        sort(nums.begin(), nums.end());
        int left = -1, right = nums.back() - nums[0];
        while (left + 1 < right) {
            int mid = (left + right) / 2;
            int cnt = 0;
            int i = 0;
            for (; i < nums.size() - 1; ++i) {
                if (nums[i + 1] - nums[i] > mid) {
                    ++cnt;
                    ++i;
                }
            }
            if (cnt >= p)
                right = mid;
            else
                left = mid;
        }
        return right;
    }
};

int main() {
    vector<int> nums = {4, 5, 2, 10, 7, 1, 4, 1};
    Solution s;
    cout << s.minimizeMax(nums, 4) << endl;
}


