package class03;

public class Code04_FindMaximumNumber {

	public static long findMaximumNumber(long k, int x) {
        if (x == 1) {
            return k + 1;
        }
		long left = 0;
		long right = (k + 1) << x;
		while (left + 1 < right) {
			long mid = (left + right) >>> 1;
			if (countDigitOne(mid) <= k) {
				left = mid;
			} else {
				right = mid;
			}
		}
		return left;
	}

	public static long countDigitOne(long num) {
		long cnt = 0;
		for (long i = 1; i <= num; i++) {
			cnt += countOnesInBinary(i);
		}
		return cnt;
	}

	public static int countOnesInBinary(long num) {
		int cnt = 0;
		for (long i = num; i != 0; i >>>= 1) {
			cnt += i & 1;
		}
		return cnt;
	}

	public static void main(String[] args) {
		long k = 15;
		int x = 1;
		System.out.println(findMaximumNumber(k, x));
		k = 15;
		x = 2;
		System.out.println(findMaximumNumber(k, x));
		k = 15;
		x = 3;
		System.out.println(findMaximumNumber(k, x));
		k = 15;
		x = 4;
		System.out.println(findMaximumNumber(k, x));
	}

}

