    def numberOfAlternatingGroups(self, a: List[int], queries: List[List[int]]) -> List[int]:
        n = len(a)
        sl = SortedList()
        t = FenwickTree(n)

        # op=1，添加一个结束位置 i
        # op=-1，移除一个结束位置 i
        def update(i: int, op: int) -> None:
            idx = sl.bisect_left(i)
            pre = sl[idx - 1]
            nxt = sl[idx % len(sl)]

            t.update((nxt - pre - 1) % n + 1, -op)  # 移除/添加旧长度
            t.update((i - pre) % n, op)
            t.update((nxt - i) % n, op)  # 添加/移除新长度

        # 添加一个结束位置 i
        def add(i: int) -> None:
            if not sl:
                t.update(n, 1)
            else:
                update(i, 1)
            sl.add(i)

        # 移除一个结束位置 i
        def remove(i: int) -> None:
            sl.remove(i)
            if not sl:
                t.update(n, -1)
            else:
                update(i, -1)

        for i, c in enumerate(a):
            if c == a[(i + 1) % n]:
                add(i)  # i 是一个结束位置

        ans = []
        for q in queries:
            if q[0] == 1:
                if not sl:
                    ans.append(n)  # 每个长为 size 的子数组都符合要求
                else:
                    cnt, s = t.query(q[1])
                    ans.append(s - cnt * (q[1] - 1))
