int addMinimum(string word) {
	int n = word.size();
	vector<int> d(n + 1);
	for (int i = 1; i <= n; i++) {
		d[i] = d[i - 1] + 1;
		for (int j = i; j >= 1; j--) {
			if (word[j - 1] == 'a') {
				if (word[i - 1] == 'b') {
					d[i] = min(d[i], d[j - 1] + 1);
				}
				else if (word[i - 1] == 'c') {
					d[i] = min(d[i], d[j - 1]);
				}
			}
			else if (word[j - 1] == 'b') {
				if (word[i - 1] == 'a') {
					d[i] = min(d[i], d[j - 1]);
				}
				else if (word[i - 1] == 'c') {
					d[i] = min(d[i], d[j - 1] + 1);
				}
			}
			else if (word[j - 1] == 'c') {
				if (word[i - 1] == 'b') {
					d[i] = min(d[i], d[j - 1]);
				}
				else if (word[i - 1] == 'a') {
					d[i] = min(d[i], d[j - 1] + 1);
				}
			}
		}
	}
	return d[n];
}
};
