	value int
	next *node
}

type list struct {
	head *node
	tail *node
}

func (l *list) append(value int) {
	n := new(node)
	n.value = value
	if l.head == nil {
		l.head = n
	} else {
		l.tail.next = n
	}
	l.tail = n
}

func find(l *list, x int) *node {
	n := l.head
	for ;n != nil; n = n.next {
		if n.value == x {
			break
		}
	}
	return n
