def minSum(nums1, nums2):
    sum1, sum2 = sum(nums1), sum(nums2)
    zero1 = sum(1 for num in nums1 if num == 0)
    zero2 = sum(1 for num in nums2 if num == 0)
    if sum1 == sum2:
        return sum1
    if zero1 == 0 and zero2 == 0:
        return -1
    if zero1 == 0:
        return sum2
    if zero2 == 0:
        return sum1
    if zero1 > zero2:
        return sum2 + zero1 - zero2
    return sum1 + zero2 - zero1


