class Solution {
public:
    int paintWalls(vector<int>& cost, vector<int>& time) {
        int n = cost.size();
        vector<int> f(n * 2 + 1, INT_MAX / 2);
        f[n] = 0;
        for (int i = 0; i < n; ++i) {
            vector<int> g(n * 2 + 1, INT_MAX / 2);
            for (int j = 0; j <= n * 2; ++j) {
                if (j > 0) {
                    g[j - 1] = min(g[j - 1], f[j]);
                }
                if (j >= time[i]) {
                    g[j] = min(g[j], f[j - time[i]] + cost[i]);
                }
            }
            f = move(g);
        }
        return *min_element(f.begin() + n, f.end());
    }
};

