class Solution {
    private long ans;
    public long maximumScoreAfterOperations(int[][] edges, int[] values) {
        List<Integer>[] g = new ArrayList[values.length];
        Arrays.setAll(g, e -> new ArrayList<>());
        g[0].add(-1);
        for (int[] e : edges) {
            int x = e[0], y = e[1];
            g[x].add(y);
            g[y].add(x);
        }
        for (int v : values) {
            ans += v;
        }
        for (int i = 0; i < values.length; ++i) {
            if (g[i].size() == 1) {
                dfs(i, -1, g, values);
            }
        }
        return ans;
    }
    private long dfs(int x, int fa, List<Integer>[] g, int[] values) {
        if (g[x].size() == 1) {
            return values[x];
        }
        long loss = 0;
        for (int y : g[x]) {
            if (y != fa) {
                loss += dfs(y, x, g, values);
            }
        }
        long res = Math.min(values[x], loss);
        if (res == values[x]) {
            ans -= values[x];
        }
        values[x] = 0;
        return res;
    }
}

