class Solution:
    def rangeSumBST(self, root: TreeNode, L: int, R: int) -> int:
        def dfs(root, l, r):
            if not root: return 0
            if root.val >= l and root.val <= r:
                return root.val + dfs(root.left, l, r) + dfs(root.right, l, r)
            elif root.val > r:
                return dfs(root.left, l, r)
            elif root.val < l:
                return dfs(root.right, l, r)
        return dfs(root, L, R)

