                    ans.append(n)  # 每个长为 size 的子数组都符合要求
                else:
                    cnt, s = t.query(q[1])
                    ans.append(s - cnt * (q[1] - 1))
            else:
                i, c = q[1], q[2]
                if a[i] == c:  # 无影响
                    continue
                pre, nxt = (i - 1) % n, (i + 1) % n
                # 修改前，先去掉结束位置
                if a[pre] == a[i]:
